mapper.select:
select count(*)
from STUDENT S where S.STUDENT_DEL = 1 and (select
COUNT(*) from TEST T where T.STUDENT_ID = S.STUDENT_ID

		and T.COURSE_ID = #{courseId,jdbcType=VARCHAR}

	) > 0


		and STUDENT_ID = #{studentId,jdbcType=VARCHAR}

		select * from (
		select S.STUDENT_ID,S.STUDENT_NAME,(select sum(TEST_SCORE) from TEST T where
		T.STUDENT_ID = S.STUDENT_ID and T.TEST_SCORE >=0) AS STUDENT_SCORE, (select count(*) from TEST T where
		T.STUDENT_ID = S.STUDENT_ID and T.TEST_SCORE = -1) AS STUDENT_NOSCORE, ROW_NUMBER() OVER (ORDER BY STUDENT_AT
		desc)
		as r from
		STUDENT S
		where S.STUDENT_DEL = 1 and (select COUNT(*)
		from
		TEST
		T where T.STUDENT_ID = S.STUDENT_ID

				and T.COURSE_ID = #{courseId,jdbcType=VARCHAR}


				and T.RULD_ID = #{studentName,jdbcType=VARCHAR}

		) > 0


				and STUDENT_ID = #{studentId,jdbcType=VARCHAR}



		) as t 

            where r>#{begin} and r<=#{end}

