select top ${size} * from STUDENT z left join STUDENTONE t on z.STUDENTONE_ID=t.STUDENTONE_ID left join STUDENTTWO tt on z.STUDENTTWO_ID=tt.STUDENTTWO_ID left join STUDENTHREE ttt on z.STUDENTHREE_ID=ttt.STUDENTHREE_ID where z.STUDENT_ID not in ( select top ${page} STUDENT_ID from STUDENT z left join STUDENTONE t on z.STUDENTONE_ID=t.STUDENTONE_ID left join STUDENTTWO tt on z.STUDENTTWO_ID=tt.STUDENTTWO_ID left join STUDENTHREE ttt on z.STUDENTHREE_ID=ttt.STUDENTHREE_ID where STUDENT_DEL=1 and STUDENT_TYPE=1 and z.STUDENT_COMPANYID=#{studentCompanyid,jdbcType=VARCHAR}
and STUDENT_DEPARTMENT = #{studentDepartment,jdbcType=INTEGER}
order by z.STUDENT_AT desc ) and STUDENT_DEL=1 and STUDENT_TYPE=1 and z.STUDENT_COMPANYID=#{studentCompanyid,jdbcType=VARCHAR}
and STUDENT_DEPARTMENT = #{studentDepartment,jdbcType=INTEGER}
order by z.STUDENT_AT desc
